      p r o g r a m  t e s t
      realxray,zebra
      read(*,*) x ray,zebra
      i f ( xray . eq . zebra ) w r i t e(*,*)                    xray *
     $* zebra
      if( xray .e q. zebra) write(*,*) 'h e l l o ' / / ' t h e r e'
      if( xray / = zebra ) write(* , *) xray + zebra
      if( xray < zebra ) write(* , *) xray - zebra
      end
